.scan-summary-box h3::after, .key-recommendations-box h3::after, .ai-advisor-box h3::after { content: ' ▼'; font-size: 0.65em; position: absolute; right: 10px; top: 50%; transform: translateY(-50%); }
.scan-summary-box h3.collapsed::after, .key-recommendations-box h3.collapsed::after, .ai-advisor-box h3.collapsed::after { transform: translateY(-50%) rotate(-90deg); }
.toc { margin-bottom: 40px; border: 1px solid #d1d5db; padding: 22px; background-color: #f9fafb; border-radius: 8px; }
.toc h3 { margin-top:0; color: #374151; }
.toc ul { list-style-type: none; padding-left: 0; columns: 2; -webkit-columns: 2; -moz-columns: 2; column-gap: 30px; }
.toc li a { display: block; padding: 5px 0; color: #4b5563; } .toc li a:hover { color: #1f2937; }
.footer { text-align: center; margin-top: 40px; padding-top: 20px; border-top: 1px solid #e1e8ed; font-size: 0.9em; color: #707B7C; }
#graph-container { width: 100%; height: 600px; border: 1px solid #ddd; margin-top: 20px; background-color: #f9f9f9; border-radius: 8px; box-shadow: 0 2px 5px rgba(0,0,0,0.05); }
